const API_URL = 'http://localhost:8082';

const getHeaders = () => {
  const token = localStorage.getItem('token');
  return {
    'Content-Type': 'application/json',
    'Authorization': token ? `Bearer ${token}` : '',
  };
};

export const api = {
  // Auth endpoints
  login: (credentials) =>
    fetch(`${API_URL}/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(credentials),
    }),

  register: (userData) =>
    fetch(`${API_URL}/register`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData),
    }),
    getDashboardStats: () =>
      fetch(`${API_URL}/dashboard/stats`, {
        headers: getHeaders(),
      }),
  // Customer endpoints
  getCustomers: () =>
    fetch(`${API_URL}/customers`, {
      headers: getHeaders(),
    }),

  createCustomer: (customerData) =>
    fetch(`${API_URL}/customers`, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify(customerData),
    }),

  updateCustomer: (id, customerData) =>
    fetch(`${API_URL}/customers/${id}`, {
      method: 'PUT',
      headers: getHeaders(),
      body: JSON.stringify(customerData),
    }),

  deleteCustomer: (id) =>
    fetch(`${API_URL}/customers/${id}`, {
      method: 'DELETE',
      headers: getHeaders(),
    }),

  // Lead endpoints
  getLeads: () =>
    fetch(`${API_URL}/leads`, {
      headers: getHeaders(),
    }),

  createLead: (leadData) =>
    fetch(`${API_URL}/leads`, {
      method: 'POST',
      headers: getHeaders(),
      body: JSON.stringify(leadData),
    }),

  updateLead: (id, leadData) =>
    fetch(`${API_URL}/leads/${id}`, {
      method: 'PUT',
      headers: getHeaders(),
      body: JSON.stringify(leadData),
    }),

  deleteLead: (id) =>
    fetch(`${API_URL}/leads/${id}`, {
      method: 'DELETE',
      headers: getHeaders(),
    }),
};

export default api;
