import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Grid, 
  Card, 
  CardContent, 
  Typography, 
  CircularProgress,
  Paper
} from '@mui/material';
import { 
  PeopleAlt as PeopleIcon,
  Business as BusinessIcon,
  TrendingUp as LeadsIcon,
  AttachMoney as RevenueIcon 
} from '@mui/icons-material';
import { userService, customerService, leadService } from '../../services/api';

const StatCard = ({ title, count, icon, loading, color }) => (
  <Card sx={{ height: '100%' }}>
    <CardContent>
      <Box display="flex" alignItems="center" justifyContent="space-between">
        <Box>
          <Typography color="textSecondary" gutterBottom variant="h6">
            {title}
          </Typography>
          {loading ? (
            <CircularProgress size={20} />
          ) : (
            <Typography variant="h4">{count}</Typography>
          )}
        </Box>
        <Box sx={{ 
          backgroundColor: `${color}15`, 
          p: 1, 
          borderRadius: 2,
          display: 'flex',
          alignItems: 'center'
        }}>
          {icon}
        </Box>
      </Box>
    </CardContent>
  </Card>
);

const Dashboard = () => {
  const [stats, setStats] = useState({
    users: 0,
    customers: 0,
    leads: 0,
    revenue: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [users, customers, leads] = await Promise.all([
          userService.getAllUsers(),
          customerService.getAllCustomers(),
          leadService.getAllLeads()
        ]);

        setStats({
          users: users.data.length,
          customers: customers.data.length,
          leads: leads.data.length,
          revenue: calculateRevenue(customers.data)
        });
      } catch (error) {
        console.error('Error fetching dashboard stats:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  const calculateRevenue = (customers) => {
    // Example revenue calculation
    return customers.reduce((total, customer) => total + (customer.value || 0), 0);
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Dashboard
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Users"
            count={stats.users}
            icon={<PeopleIcon sx={{ color: '#1976d2' }} />}
            loading={loading}
            color="#1976d2"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Customers"
            count={stats.customers}
            icon={<BusinessIcon sx={{ color: '#2e7d32' }} />}
            loading={loading}
            color="#2e7d32"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Active Leads"
            count={stats.leads}
            icon={<LeadsIcon sx={{ color: '#ed6c02' }} />}
            loading={loading}
            color="#ed6c02"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Revenue"
            count={`$${stats.revenue.toLocaleString()}`}
            icon={<RevenueIcon sx={{ color: '#9c27b0' }} />}
            loading={loading}
            color="#9c27b0"
          />
        </Grid>
      </Grid>

      <Grid container spacing={3} sx={{ mt: 2 }}>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Recent Activity
            </Typography>
            {/* Add recent activity content here */}
          </Paper>
        </Grid>
        <Grid item xs={12} md={6}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Quick Actions
            </Typography>
            {/* Add quick actions content here */}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
