import React, { useState, useEffect } from 'react';
import { Box, Button } from '@mui/material';
import { Add as AddIcon } from '@mui/icons-material';
import PageHeader from '../../components/common/PageHeader';
import SearchBar from '../../components/common/SearchBar';
import FilterBar from '../../components/common/FilterBar';
import DataTable from '../../components/common/DataTable';
import CustomerForm from './CustomerForm';
import api from '../../services/api';

const Customers = () => {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [openForm, setOpenForm] = useState(false);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [filters, setFilters] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);

  const columns = [
    { id: 'name', label: 'Name' },
    { id: 'company', label: 'Company' },
    { id: 'email', label: 'Email' },
    { id: 'phone', label: 'Phone' },
    { id: 'status', label: 'Status' },
    {
      id: 'actions',
      label: 'Actions',
      render: (_, customer) => (
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Button
            size="small"
            onClick={() => handleEdit(customer)}
          >
            Edit
          </Button>
          <Button
            size="small"
            color="error"
            onClick={() => handleDelete(customer.id)}
          >
            Delete
          </Button>
        </Box>
      ),
    },
  ];

  const filterOptions = [
    { id: 'status', label: 'Status', options: ['Active', 'Inactive'] },
    { id: 'type', label: 'Customer Type', options: ['Individual', 'Company'] },
  ];

  useEffect(() => {
    fetchCustomers();
  }, [page, rowsPerPage, filters, searchTerm]);

  const fetchCustomers = async () => {
    try {
      setLoading(true);
      const response = await api.getCustomers({
        page,
        limit: rowsPerPage,
        filters,
        search: searchTerm,
      });
      const data = await response.json();
      if (response.ok) {
        setCustomers(data.customers);
      }
    } catch (error) {
      console.error('Error fetching customers:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleAdd = () => {
    setSelectedCustomer(null);
    setOpenForm(true);
  };

  const handleEdit = (customer) => {
    setSelectedCustomer(customer);
    setOpenForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this customer?')) {
      try {
        const response = await api.deleteCustomer(id);
        if (response.ok) {
          fetchCustomers();
        }
      } catch (error) {
        console.error('Error deleting customer:', error);
      }
    }
  };

  const handleSubmit = async (customerData) => {
    try {
      if (selectedCustomer) {
        await api.updateCustomer(selectedCustomer.id, customerData);
      } else {
        await api.createCustomer(customerData);
      }
      setOpenForm(false);
      fetchCustomers();
    } catch (error) {
      console.error('Error saving customer:', error);
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <PageHeader
        title="Customers"
        action={true}
        actionText="Add Customer"
        actionIcon={<AddIcon />}
        onActionClick={handleAdd}
      />

      <Box sx={{ mb: 3 }}>
        <SearchBar
          onSearch={setSearchTerm}
          placeholder="Search customers..."
        />
        <FilterBar
          filters={filterOptions}
          activeFilters={filters}
          onFilterChange={(id, value) => setFilters({ ...filters, [id]: value })}
          onClearFilters={() => setFilters({})}
        />
      </Box>

      <DataTable
        columns={columns}
        data={customers}
        loading={loading}
        page={page}
        rowsPerPage={rowsPerPage}
        onPageChange={setPage}
        onRowsPerPageChange={setRowsPerPage}
      />

      <CustomerForm
        open={openForm}
        customer={selectedCustomer}
        onClose={() => setOpenForm(false)}
        onSubmit={handleSubmit}
      />
    </Box>
  );
};

export default Customers;
